import React, {Component} from 'react';
import {connect} from 'react-redux';
import './app.css';
import Cardlist from '../components/Cardlist.js';
import Searchbox from '../components/Searchbox.js';
import Scroll from '../components/Scroll.js';
import ErrorBoundries from '../components/ErrorBoundries.js';
import {setSetSearchfield} from './actions.js';

const mapStateToProps = (state) => {
	return({
		searchfield: state.searchfield
	})
};

const mapDispatchToProps = (dispatch) =>{
	return({
		onSearchChange: (e) => {dispatch(setSetSearchfield(e.target.value))}
	})
};

class App extends Component {
	constructor(){
    super();
    this.state={robots:[]}
	}

	componentDidMount(){
		fetch("https://jsonplaceholder.typicode.com/users")
		.then((res)=>{return res.json()})
		.then((users)=>{return this.setState({robots: users})})
	}	

    /* THIS IS AN EXAMPLE OF AN ACTION */
    /*onSearchChange =(e)=> {
    	this.setState({searchfield:e.target.value});
    	}*/

	render(props){
	const {robots} = this.state;
	const {onSearchChange, searchfield} = this.props;
	const filtRobo = robots.filter((robots)=>{
    return(robots.name.toLowerCase().includes(searchfield.toLowerCase()))})

	if(robots.length === 0){
		return(<h1>{`loading`}</h1>)}
	else{
		return(
    <div className={`tc`}>
	<h1 className={`f1`}>robofriends</h1>
	<Searchbox searchfield={searchfield} 
	onChange={onSearchChange}/> 
	<Scroll>
	<ErrorBoundries>
	<Cardlist robots={filtRobo}/>
	</ErrorBoundries>
	</Scroll>
	</div>)}}}

export default connect(mapStateToProps, mapDispatchToProps)(App);
